import { IconButton, Button } from "@mui/material";

import React, { useState } from "react";
import "../Components/todo.css";

const TodoList = () => {
  const [activity, setActivity] = useState(" ");
  const [newitem, setNewItem] = useState([]);

  const itemEvent = (event) => {
    setItem(event.target.value);
  };
  const addData = () => {
    setNewItem((prevalue) => {
      return [...prevalue, item];
    });
    setItem(" ");
  };

  function deleteData(i) {
    const updatedListData = newitem.filter((element, id) => {
      return i != id;
    });
    setNewItem(updatedListData);
  }
  return (
    <>
      <div className="Main_div">
        <div className="Center_div">
          <div className="head-div">
            <h1 variant="h3" className="heading">
              Todo List
            </h1>
            <h1 variant="h3" className="heading">
              Todo List
            </h1>
          </div>

          <div className="input-box">
            <input
              type="text"
              onChange={itemEvent}
              value={item}
              placeholder="Enter Your Todos..."
            />
            <Button onClick={addData} variant="contained">
              Add
            </Button>
          </div>

          <div className="output">
            {newitem != [] &&
              newitem.map((Data, i) => {
                return (
                  <>
                    <div key={i} className="outinner">
                      <div className="listdata">{Data}</div>
                      <div className="Remove-div">
                        <Button onClick={() => deleteData} variant="contained">
                          Delte
                        </Button>
                      </div>
                    </div>
                  </>
                );
              })}
          </div>
        </div>
      </div>
    </>
  );
};

export default TodoList;
